import styled from 'styled-components';

export const SignUpContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 380px;
  @media screen and (max-width: 800px) {
    align-items: center;
  }
`;

export const SignUpTitle = styled.h2`
  margin: 10px 0;
`;

export const CustomButton = styled.div`
  @media screen and (max-width: 800px) {
    margin-bottom: 5px;
  }
`;
